class Solution {
public:
    int magicalString(int n) {
        vector<int> list(max(3, n));
        list[0] = 1; list[1] = list[2] = 2;
        int ans = 1, i1 = 2, i2 = 3, surplus = 2, curVal = 1, curMode = 1;
        while (i2 < n) {
            if (surplus == 0) {
                surplus = list[++i1];
                curVal += curMode;
                curMode = -curMode;
            }
            list[i2++] = curVal;
            surplus--;
            if (curVal == 1) ans++;
        }
        return ans;
    }
};
