function licenseKeyFormatting(s: string, k: number): string {
  s = s.toUpperCase().replace(/-/g, '');
  const sum = s.length;
  if (sum <= k) return s;
  const mod = sum % k || k;
  let ans = s.substr(0, mod) + '-';
  for (let i = 0, end = sum - mod; i < end; i++) {
    if (i !== 0 && i % k === 0) ans += '-';
    ans += s[i + mod];
  }
  return ans;
}
