class Solution {
   public:
    vector<int> findDiagonalOrder(vector<vector<int>>& mat) {
        int n = mat.size(), m = mat[0].size(), i = 0, j = 0;
        vector<int> ans;
        while (i != n - 1 || j != m - 1) {
            while (i != -1 && j != m) ans.push_back(mat[i--][j++]);
            i++;
            if (j == m) j--, i++;
            if (i == n - 1 && j == m - 1) break;
            while (i != n && j != -1) ans.push_back(mat[i++][j--]);
            j++;
            if (i == n) i--, j++;
            if (i == n - 1 && j == m - 1) break;
        }
        ans.push_back(mat[i][j]);
        return ans;
    }
};
