class Solution {
   public:
    bool checkPerfectNumber(int num) {
        if (num == 1) return 0;
        int ans = 1;
        for (int i = 2; i * i <= num; i++) {
            if (num % i) continue;
            ans += i;
            if (i * i < num) ans += num / i;
        }
        return ans == num;
    }
};
