var fib = function (N) {
  let obj = {
    0: 0,
    1: 1,
  };
  for (let i = 2; i <= N; i++) {
    obj[i] = obj[i - 1] + obj[i - 2];
  }
  return obj[N];
};

function fib(n: number): number {
  if (n === 0) return 0;
  if (n === 1) return 1;
  let num0 = 0;
  let num1 = 1;
  for (let i = 2; i <= n; i++) [num0, num1] = [num1, num0 + num1];
  return num1;
}
