function findMinMoves(machines: number[]): number {
  const sum = machines.reduce((total, cur) => total + cur, 0);
  const n = machines.length;
  if (sum % n !== 0) return -1;
  const avg = ~~(sum / n);
  let ans = 0;
  let num = 0;
  for (let i = 0; i < n; i++) {
    num += machines[i] - avg;
    ans = Math.max(ans, Math.max(machines[i] - avg, Math.abs(num)));
  }
  return ans;
}
