function checkSubarraySum(nums: number[], k: number): boolean {
  const len = nums.length;
  if (len < 2 || k === 0) return false;
  const map = new Map<number, number>([[0, -1]]);
  let num = 0;
  for (let i = 0; i < len; i++) {
    num = (num + nums[i]) % k;
    let prev = map.get(num);
    if (prev !== undefined) {
      if (i - prev >= 2) return true;
    } else map.set(num, i);
  }
  return false;
}
