const random = () => (~~(Math.random() * 10000)).toString();
const map: Record<string, string> = {};
function encode(longUrl: string): string {
  let s = random();
  while (map[s]) s = random();
  map[s] = longUrl;
  return s;
}
function decode(shortUrl: string): string {
  return map[shortUrl];
}
