class Solution {
   public:
    int h(TreeNode* node) {
        if (!node) return 0;
        return max(h(node->left), h(node->right)) + 1;
    }
    int diameterOfBinaryTree(TreeNode* root) {
        if (!root) return 0;
        return max(h(root->left) + h(root->right),
                   max(diameterOfBinaryTree(root->left),
                       diameterOfBinaryTree(root->right)));
    }
};
