function checkRecord(s: string): boolean {
  let ac = 0;
  let lc = 0;
  for (const c of s) {
    if (c === 'A') {
      if (++ac >= 2) return false;
      lc = 0;
    } else if (c === 'L') {
      if (++lc >= 3) return false;
    } else {
      lc = 0;
    }
  }
  return true;
}

class Solution:
    def checkRecord(self, s: str) -> bool:
        return s.count('A') < 2 and 'LLL' not in s
