class Solution {
   public:
    int nextGreaterElement(int n) {
        string str = to_string(n);
        int len = str.size(), i = len - 2, j = len - 1;
        while (i >= 0 && str[i] - '0' >= str[i + 1] - '0') i--;
        if (i == -1) return -1;
        while (j > i && str[j] - '0' <= str[i] - '0') j--;
        swap(str[i], str[j]);
        reverse(str.begin() + i + 1, str.end());
        long num = stol(str);
        return num > INT_MAX ? -1 : num;
    }
};
