/**
 * @param {string} s
 * @return {string}
 */
var reverseWords = function (s) {
  const words = s.split(' ');
  for (let num in words) {
    words[num] = [...words[num]].reverse().join('');
  }
  return words.join(' ');
};

function reverseWords(s: string): string {
  return s
    .split(' ')
    .map(v => v.split('').reverse().join(''))
    .join(' ');
}
