function matrixReshape(nums: number[][], r: number, c: number): number[][] {
  const sum = nums.length * nums[0].length;
  if (sum !== r * c) return nums;
  const arr = nums.reduce((totalArr, arr) => {
    totalArr.push(...arr);
    return totalArr;
  }, [] as number[]);
  const ans: number[][] = [];
  for (let i = 0; i < r; i++) {
    const temp: number[] = [];
    for (let j = 0; j < c; j++) {
      temp.push(arr[i * c + j]);
    }
    ans.push(temp);
  }
  return ans;
}
