class Solution {
   public:
    string tree2str(TreeNode* root) {
        if (!root) return "";
        string l = "(" + tree2str(root->left) + ")",
               r = "(" + tree2str(root->right) + ")";
        return to_string(root->val) + (l == "()" && r == "()"   ? ""
                                       : l != "()" && r == "()" ? l
                                                                : l + r);
    }
};
