function triangleNumber(nums: number[]): number {
  const n = nums.length;
  nums.sort((a, b) => a - b);
  let ans = 0;
  for (let i1 = 0; i1 < n - 1; i1++) {
    const num1 = nums[i1];
    for (let i2 = i1 + 1; i2 < n - 1; i2++) {
      const num2 = nums[i2];
      if (num1 + num2 > nums[n - 1]) {
        ans += n - 1 - i2;
        continue;
      }
      let l = i2 + 1;
      let r = n - 1;
      while (l < r) {
        const mid = (l + r) >> 1;
        if (num1 + num2 <= nums[mid]) r = mid;
        else l = mid + 1;
      }
      ans += l - 1 - i2;
    }
  }
  return ans;
}
