function minSteps(n: number): number {
  if (n === 1) return 0;
  const dp = new Array(n + 1).fill(0);
  for (let num = 2; num <= n; num++) {
    if (num % 2 === 0) {
      dp[num] = dp[num / 2] + 2;
      continue;
    }
    for (let i = num - 1; i >= 2; i--) {
      if (num % i === 0) {
        dp[num] = dp[i] + num / i;
        break;
      }
    }
    if (dp[num] === 0) dp[num] = num;
  }
  return dp[n];
}
