function judgeCircle(moves: string): boolean {
  let x = 0;
  let y = 0;
  const moveFn: Record<string, () => void> = {
    U: () => (y += 1),
    D: () => (y -= 1),
    R: () => (x += 1),
    L: () => (x -= 1),
  };
  for (const move of moves) moveFn[move]();
  return x === 0 && y === 0;
}
