function gcd(a: number, b: number): number {
  if (a < b) return gcd(b, a);
  if (b) return gcd(b, a % b);
  return a;
}
function subarrayGCD(nums: number[], k: number): number {
  let ans = 0;
  const n = nums.length;
  for (let i = 0; i < n; i++) {
    if (nums[i] === k) {
      ans++;
    }
    let num = nums[i];
    for (let j = i - 1; j >= 0; j--) {
      num = gcd(nums[j], num);
      if (num === k) ans++;
    }
  }
  return ans;
}
