class Solution {
public:
    long long totalCost(vector<int>& costs, int k, int candidates) {
        int n = costs.size();
        int lmax = candidates, rmax = n - 1 - candidates;
        priority_queue<int, vector<int>, greater<int>> ql, qr, qa;
        for (int i = 0; i < candidates; i++) {
            ql.push(costs[i]);
            qr.push(costs[n - 1 - i]);
        }
        if (lmax > rmax) {
            for (int i = 0; i < n; i++) {
                qa.push(costs[i]);
            }
        }
        long long sum = 0;
        for (int i = 0; i < k; i++) {
            // cout << "i = " << i << endl;
            if (lmax > rmax) {
                sum += qa.top();
                qa.pop();
            } else {
                // cout << "lmax = " << lmax << ", rmax = " << rmax << endl;
                if (ql.top() <= qr.top()) {
                    sum += ql.top();
                    ql.pop();
                    ql.push(costs[lmax]);
                    lmax++;
                } else {
                    sum += qr.top();
                    qr.pop();
                    qr.push(costs[rmax]);
                    rmax--;
                }
                if (lmax > rmax) {
                    while (ql.size()) {
                        qa.push(ql.top());
                        ql.pop();
                    }
                    while (qr.size()) {
                        qa.push(qr.top());
                        qr.pop();
                    }
                }
                // cout << "lmax = " << lmax << ", rmax = " << rmax << endl;
            }
        }

        return sum;
    }
};
