class Solution {
public:
    int gcd(int a, int b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }
    int subarrayLCM(vector<int>& nums, int k) {
        int n = nums.size(), ans = 0;
        for (int i = 0; i < n; i++) {
            int cur_gcd = nums[i], num = nums[i];
            for (int j = i; j < n; j++) {
                num = num * nums[j] / gcd(num, nums[j]);
                if (num == k) ans++;
                if (num > k) break;
            }
        }
        return ans;
    }
};
