typedef long long ll;

using namespace std;

struct City {
    int len, idx, size;
    unordered_set<int> next;
    City(): len(0), idx(0), size(1) {}
};
class Solution {
public:
    vector<City> list;
    vector<int> idxlist;
    long long minimumFuelCost(vector<vector<int>>& roads, int seats) {
        int n = roads.size() + 1;
        if (n == 1) return 0;
        list = vector<City>(n);
        idxlist = vector<int>(n);
        for (int i = 0; i < n; i++) {
            list[i].idx = i;
            idxlist[i] = i;
        }
        for (auto &road : roads) {
            list[road[0]].next.insert(road[1]);
            list[road[1]].next.insert(road[0]);
        }
        initLen();
        // for (int i = 0; i < n; i++) {
        //     cout << "i = " << i
        //          << ", len = " << list[i].len
        //          << ", idx = " << list[i].idx
        //          << ", next = ";
        //     for (auto &next : list[i].next) {
        //         cout << next << ", ";
        //     }
        //     cout << endl;
        // }
        sort(idxlist.begin(), idxlist.end(), [&](auto &a, auto &b){
            return list[a].len < list[b].len;
        });
        // cout << "-====" << endl;
        long long ans = 0;
        for (int idx = n - 1; idx > 0; idx--) {
            int i = idxlist[idx];
            ans += 1 + (list[i].size - 1) / seats;
            int next = *list[i].next.begin();
            list[next].size += list[i].size;
            list[next].next.erase(i);
            // cout << "i = " << i
            //      << ", idx = " << list[i].idx
            //      << ", next = " << list[next].idx
            //      << ", cursize = " << list[i].size
            //      << ", nextsize = " << list[next].size
            //      << endl;
        }
        return ans;
    }
    void initLen() {
        queue<int> q;
        q.push(0);
        int size = 1, cur = 1;
        while (q.size()) {
            int node = q.front();
            q.pop();
            for (auto &next : list[node].next) {
                if (next == 0 || list[next].len != 0) continue;
                list[next].len = cur;
                q.push(next);
            }
            if (--size == 0) {
                size = q.size();
                cur++;
            }
        }
    }
};
