class Solution {
public:
    int deleteGreatestValue(vector<vector<int>>& grid) {
        for (auto &item : grid) sort(item.begin(), item.end());
        int ans = 0, n = grid.size(), m = grid[0].size();
        for (int i = 0; i < m; i++) {
            int num = 0;
            for (int j = 0; j < n; j++) {
                num = max(num, grid[j][i]);
            }
            ans += num;
        }
        return ans;

    }
};
