class Solution {
public:
    int n, k;
    vector<int> price;
    int maximumTastiness(vector<int>& price, int k) {
        sort(price.begin(), price.end());
        n = price.size();
        this->k = k;
        this->price = price;
        int l = 0, r = price[n - 1] - price[0], m;
        while (l < r) {
            m = (l + r + 1) / 2;
            if (check(m)) l = m;
            else r = m - 1;
        }
        return l;
    }
    bool check(int num) {
        int cnt = 1, cur = price[0];
        for (int i = 1; i < n; i++) {
            if (cur + num <= price[i]) {
                cnt++;
                cur = price[i];
            }
        }
        return cnt >= k;
    }
};
