class Solution {
public:
    int countDigits(int num) {
        int ans = 0;
        for (int v = num; v; v /= 10) {
            int sub = v % 10;
            if (num % sub == 0) ans++;
        }
        return ans;
    }
};

impl Solution {
    pub fn count_digits(num: i32) -> i32 {
        let mut ans = 0;
        let mut tmp = num;
        while tmp != 0 {
            let v = tmp % 10;
            if num % v == 0 {
                ans += 1;
            }
            tmp /= 10;
        }
        ans
    }
}
