class Solution {
   public:
    bool isOneBitCharacter(vector<int>& bits) {
        int idx = 0;
        while (idx < bits.size() - 1) {
            if (bits[idx] == 0)
                idx += 1;
            else
                idx += 2;
        }
        return idx == bits.size() - 1;
    }
};
