class Solution {
   public:
    char nextGreatestLetter(vector<char>& letters, char target) {
        int list[26] = {0};
        for (auto& ch : letters) {
            list[ch - 'a'] = 1;
        }
        target -= 'a';
        for (int i = (target + 1) % 26, cnt = 0; cnt < 26;
             cnt++, i = (i + 1) % 26) {
            if (list[i]) return i + 'a';
        }
        return 'a';
    }
};

class Solution {
   public:
    char nextGreatestLetter(vector<char>& letters, char target) {
        int l = 0, r = letters.size(), m;
        while (l < r) {
            m = (l + r) >> 1;
            if (letters[m] > target)
                r = m;
            else
                l = m + 1;
        }
        return l == letters.size() ? letters[0] : letters[l];
    }
};
