class Solution {
public:
    int maxChunksToSorted(vector<int>& arr) {
        vector<int> s;
        for (auto &num : arr) {
            if (s.empty() || s[s.size() - 1] <= num) {
                s.push_back(num);
            } else {
                int num_max = s[s.size() - 1];
                while (s.size() && s[s.size() - 1] > num) s.pop_back();
                s.push_back(num_max);
            }
        }
        return s.size();
    }
};
