function kthSmallestPrimeFraction(arr: number[], k: number): number[] {
  const n = arr.length;
  const list: number[][] = [];
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      list.push([arr[i], arr[j]]);
    }
  }
  list.sort(([num11, num12], [num21, num22]) => num11 / num12 - num21 / num22);
  return list[k - 1];
}
