class Solution {
public:
    string customSortString(string order, string s) {
        int list[26] = {0};
        for (int i = 0; i < order.size(); i++) list[order[i] - 'a'] = i;
        sort(s.begin(), s.end(), [&list](char &a, char &b){ return list[a - 'a'] < list[b - 'a']; });
        return s;
    }
};
