class Solution {
public:
    int numMatchingSubseq(string s, vector<string>& words) {
        vector<vector<int>> list(26);
        for (int i = 0; i < s.size(); i++) list[s[i] - 'a'].push_back(i);
        int ans = 0;
        for (auto &word : words) {
            if (word.size() > s.size()) continue;
            int p = -1;
            bool f = true;
            for (auto &c : word) {
                auto &ilist = list[c - 'a'];
                auto it = upper_bound(ilist.begin(), ilist.end(), p);
                if (it == ilist.end()) {
                    f = false;
                    break;
                }
                if (!f) break;
                p = *it;
            }
            if (f) ans++;
        }
        return ans;
    }
};
