class Solution {
public:
    int preimageSizeFZF(int k) {
        return help(k + 1) - help(k);
    }
    long long help(int k) {
        long long l = 0, r = 5LL * k;
        while (l < r) {
            long long mid = (l + r) / 2;
            if (get_cnt(mid) >= k) r = mid;
            else l = mid + 1;
        }
        return l;
    }
    int get_cnt(int num) {
        int cnt = 0;
        while (num) {
            cnt += num / 5;
            num /= 5;
        }
        return cnt;
    }
};
