class Solution {
   public:
    int list[100001] = {0};
    int bestRotation(vector<int>& nums) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            if (i >= nums[i]) {
                list[0]++;
                list[i - nums[i] + 1]--;
            }
            list[i + 1]++;
            list[min(i + n - nums[i] + 1, n)]--;
        }
        int ans = 0, ansnum = 0, sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += list[i];
            if (sum > ansnum) {
                ans = i;
                ansnum = sum;
            }
        }
        return ans;
    }
};
