const string l[26] = {".-",   "-...", "-.-.", "-..",  ".",    "..-.", "--.",
                      "....", "..",   ".---", "-.-",  ".-..", "--",   "-.",
                      "---",  ".--.", "--.-", ".-.",  "...",  "-",    "..-",
                      "...-", ".--",  "-..-", "-.--", "--.."};
class Solution {
   public:
    string translate(const string &word) {
        string ans = "";
        for (auto &ch : word) ans += l[ch - 'a'];
        return ans;
    }
    int uniqueMorseRepresentations(vector<string> &words) {
        unordered_set<string> s;
        for (auto &word : words) s.insert(translate(word));
        return s.size();
    }
};
