function lemonadeChange(bills: number[]): boolean {
  let five = 0;
  let ten = 0;
  for (const bill of bills) {
    if (bill === 5) {
      five++;
    } else if (bill === 10) {
      five--;
      ten++;
    } else if (ten > 0) {
      ten--;
      five--;
    } else {
      five -= 3;
    }
    if (five < 0) return false;
  }
  return true;
}

class Solution {
public:
    bool lemonadeChange(vector<int>& bills) {
        int coins[2] = {0};
        for (auto &bill : bills) {
            switch (bill) {
                case 5: 
                    coins[0] += 1; 
                    break;
                case 10: 
                    if (coins[0] >= 1) {
                        coins[0]--;
                    } else {
                        return false;
                    }
                    coins[1] += 1;
                    break;
                case 20:
                    if (coins[0] >= 1 && coins[1] >= 1) {
                        coins[0] -= 1;
                        coins[1] -= 1;
                    } else if (coins[0] >= 3) {
                        coins[0] -= 3;
                    } else {
                        return false;
                    }
            }
        }
        return true;
    }
};
