function transpose(matrix: number[][]): number[][] {
  const ans: number[][] = [];
  for (let i = 0, colLen = matrix[0].length; i < colLen; i++) {
    const arr: number[] = [];
    for (let j = 0, rowLen = matrix.length; j < rowLen; j++) arr.push(matrix[j][i]);
    ans.push(arr);
  }
  return ans;
}

function transpose(matrix: number[][]): number[][] {
  return matrix[0].map((_, i) => matrix.map(arr => arr[i]));
}
