class Solution:
    def minRefuelStops(self, target: int, startFuel: int, stations: List[List[int]]) -> int:
        stations.append([target, 0])
        res = 0
        cur = startFuel
        q = []
        for pos, fuel in stations:
            while q and cur < pos:
                res += 1
                cur += -heappop(q)
            if cur < pos: return -1
            heappush(q, -fuel)
        return res
