class Solution {
   public:
    int minEatingSpeed(vector<int>& piles, int h) {
        int nmin = 1, nmax = INT_MIN;
        for (auto& pile : piles) nmax = max(nmax, pile);
        while (nmin < nmax) {
            int m = (nmin + nmax) >> 1;
            int c = check(piles, m);
            if (c > h)
                nmin = m + 1;
            else
                nmax = m;
        }
        return nmin;
    }
    int check(vector<int>& piles, int target) {
        int ans = 0;
        for (auto pile : piles) {
            ans += pile / target;
            if (pile % target) ans += 1;
        }
        return ans;
    }
};
