class Solution {
   public:
    int projectionArea(vector<vector<int>>& grid) {
        int n = grid.size(), x = 0, y = 0, z = 0;
        // z
        for (int row = 0; row < n; row++) {
            for (int col = 0; col < n; col++) {
                if (grid[row][col] != 0) z++;
            }
        }
        // y
        for (int row = 0; row < n; row++) {
            int h = 0;
            for (int col = 0; col < n; col++) {
                h = max(h, grid[row][col]);
            }
            y += h;
        }
        // x
        for (int col = 0; col < n; col++) {
            int h = 0;
            for (int row = 0; row < n; row++) {
                h = max(h, grid[row][col]);
            }
            x += h;
        }
        return x + y + z;
    }
};
