function fairCandySwap(A: number[], B: number[]): number[] {
  const num =
    (A.reduce((total, cur) => total + cur, 0) - B.reduce((total, cur) => total + cur, 0)) / 2;
  const set = new Set(A);
  for (const y of B) {
    const x = y + num;
    if (set.has(x)) return [x, y];
  }
  return [];
}
