class Solution:
    @cache
    def allPossibleFBT(self, n: int) -> List[Optional[TreeNode]]:
        return [
            TreeNode(0, lnode, rnode) 
            for cnt in range(1, n - 1)
            for lnode in self.allPossibleFBT(cnt) 
            for rnode in self.allPossibleFBT(n - 1 - cnt)
        ] if n != 1 else [TreeNode(0)]
