function isMonotonic(A: number[]): boolean {
  const len = A.length - 1;
  let f = true;
  for (let i = 0; i < len; i++) {
    if (A[i] > A[i + 1]) {
      f = false;
      break;
    }
  }
  if (f) return true;
  for (let i = 0; i < len; i++) {
    if (A[i] < A[i + 1]) {
      return false;
    }
  }
  return true;
}
