class Solution {
public:
    int totalFruit(vector<int>& fruits) {
        unordered_map<int, int> m;
        int ans = 0, l = 0, r = 0, n = fruits.size();
        while (r < n) {
            m[fruits[r]]++;
            if (m.size() == 3) {
                do {
                    m[fruits[l]]--;
                    if (m[fruits[l]] == 0) m.erase(fruits[l]);
                    l++;
                } while (l < n && m.size() == 3);
            }
            ans = max(ans, r - l + 1);
            r++;
        }
        return ans;
    }
};
