class Solution {
   public:
    int numUniqueEmails(vector<string> &emails) {
        unordered_set<string> s;
        for (auto &o : emails) s.insert(format(o));
        return s.size();
    }
    string format(string email) {
        string ans = "";
        bool suffix = false, ignore = false;
        for (auto &ch : email) {
            if (ch == '+') {
                ignore = true;
            } else if (ch == '@') {
                suffix = true;
                ans += ch;
            } else if (ch == '.' && !suffix) {
                continue;
            } else if (suffix || !ignore) {
                ans += ch;
            }
        }
        return ans;
    }
};
