class Solution {
   public:
    int minDeletionSize(vector<string>& strs) {
        int ans = 0, n = strs.size(), m = strs[0].size();
        for (int i = 0; i < m; i++) {
            char prev = strs[0][i];
            for (int j = 1; j < n; j++) {
                if (strs[j][i] < prev) {
                    ans++;
                    break;
                }
                prev = strs[j][i];
            }
        }
        return ans;
    }
};

class Solution {
   public:
    int minDeletionSize(vector<string>& strs) {
        string prev = strs[0];
        int ans = 0;
        unordered_set<int> used;
        for (int i = 1; i < strs.size(); i++) {
            for (int j = 0; j < prev.size(); j++) {
                if (used.count(j)) continue;
                if (strs[i][j] < prev[j]) {
                    ans++;
                    used.insert(j);
                }
            }
            prev = strs[i];
        }
        return ans;
    }
};
