function largestPerimeter(A: number[]): number {
  A.sort((a, b) => a - b);
  const len = A.length;
  const can = (edge1: number, edge2: number, edge3: number): number =>
    edge1 + edge2 > edge3 && edge2 + edge3 > edge1 && edge1 + edge3 > edge2
      ? edge1 + edge2 + edge3
      : 0;
  for (let i = len - 1; i >= 0; i--) {
    const num = can(A[i], A[i - 1], A[i - 2]);
    if (num !== 0) return num;
  }
  return 0;
}
