function addToArrayForm(A: number[], K: number): number[] {
  let add: 1 | 0 = 0;
  const kArr: number[] = [];
  while (K !== 0) {
    kArr.push(K % 10);
    K = ~~(K / 10);
  }
  A = A.reverse();
  for (let i = 0, len = Math.max(A.length, kArr.length); i < len; i++) {
    let num = (A[i] ?? 0) + (kArr[i] ?? 0) + add;
    if (num >= 10) {
      num -= 10;
      add = 1;
    } else {
      add = 0;
    }
    A[i] = num;
  }
  add === 1 && A.push(1);
  return A.reverse();
}
