class Solution:
    def magicTower(self, nums: List[int]) -> int:
        q = []
        cur = 1
        ans = sum = 0
        for num in nums:
            if num < 0: heappush(q, num)
            while q and cur <= -num:
                ans += 1
                top = heappop(q)
                cur -= top
                sum += top
            if cur <= -num: return -1
            cur += num
        return ans if cur > -sum else -1
