class Solution {
   public:
    bool isAlienSorted(vector<string> &words, string order) {
        int nums[30] = {0};
        for (int i = 0; i < order.size(); i++) nums[order[i] - 'a'] = i;
        for (auto &str : words) {
            for (auto &ch : str) {
                ch = nums[ch - 'a'] + 'a';
            }
        }
        for (int i = 1; i < words.size(); i++) {
            if (words[i] < words[i - 1]) return 0;
        }
        return 1;
    }
};
