class Solution {
   public:
    bool isStraight(vector<int>& nums) {
        sort(nums.begin(), nums.end());
        int cnt0 = 0;
        for (int i = 0; i < nums.size() - 1; i++) {
            int num = nums[i];
            if (num == 0)
                cnt0++;
            else if (nums[i + 1] == num)
                return 0;
            else if (nums[i + 1] != num + 1) {
                if (nums[i + 1] - num - 1 > cnt0) return 0;
                cnt0 -= nums[i + 1] - num;
            }
        }
        return 1;
    }
};
