class CQueue {
  inStack: number[] = [];
  outStack: number[] = [];
  appendTail(value: number): void {
    this.inStack.push(value);
  }
  deleteHead(): number {
    if (this.outStack.length === 0) {
      if (this.inStack.length === 0) return -1;
      while (this.inStack.length !== 0) this.outStack.push(this.inStack.pop()!);
    }
    return this.outStack.pop()!;
  }
}
